import styled from 'styled-components'

export const StyledRotateButton = styled.button`
box-sizing: border-box;
margin: 0 10px 10px 0 ;
min-width: 100%;
border-radius: 20px;
border: none;
color: white;
background: #333;
font-family: Pixel, Arial, Helvetica, sand-serif;
font-size: 1px;
outline: none;
cursor: pointer;
`
